    public static function get($name, $classes = '') {
        $path = self::$icons[$name] ?? self::$icons['home']; // Fallback
        
        // Check if it's a social icon (usually fill, no stroke) or UI icon (stroke)
        $is_fill = in_array($name, ['facebook', 'twitter', 'instagram']);
        
        $attrs = $is_fill 
            ? 'fill="currentColor" viewBox="0 0 24 24"' 
            : 'fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor"';

        return '<svg xmlns="http://www.w3.org/2000/svg" class="' . $classes . '" ' . $attrs . '>' . $path . '</svg>';
    }
